.card {
  display: inline-block;
  background-color: transparent;
  vertical-align: top;
  width: 80px;
  height: 100px;
  text-align: center;
  margin: 0 5px 5px 0;
  transition: 0.5s transform ease-out;
  perspective: 1000px;
  cursor: pointer;
}
.card__inner {
  position: relative;
  width: 100%;
  height: 100%;
  text-align: center;
  transition: transform 0.8s;
  transform-style: preserve-3d;
}
.card_active .card__inner {
  transform: rotateY(180deg);
}
.card__front, .card__back {
  position: absolute;
  border-radius: 5px;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
}
.card__front {
  background-color: #f2f2f2;
  color: black;
}
.card__back {
  background-color: #f2f2f2;
  color: white;
  transform: rotateY(180deg);
}
.card:hover {
  animation-name: shake-slow;
  animation-duration: 5s;
  animation-timing-function: ease-in-out;
  animation-iteration-count: infinite;
}
.card_active:hover {
  animation: 0;
  transition: 0.5s transform ease-out;
}
.card_hide {
  visibility: hidden;
  opacity: 0;
  transition: visibility 0s linear 0.5s, opacity 0.5s linear;
}
.card__image {
  width: 80px;
  height: 100px;
  border-radius: 5px;
}

